(function(){const element=document.getElementById('countdown-days');if(element){const now=new Date();let newYear=new Date(now.getFullYear(),11,31);if(newYear<now){newYear.setFullYear(newYear.getFullYear()+1)}
const remainingDays=Math.ceil((newYear-now)/86400000);element.textContent=remainingDays;return}
const nowFallback=new Date();let newYearFallback=new Date(nowFallback.getFullYear(),11,31);if(newYearFallback<nowFallback){newYearFallback.setFullYear(newYearFallback.getFullYear()+1)}
const remainingDaysFallback=Math.ceil((newYearFallback-nowFallback)/86400000);document.currentScript.insertAdjacentHTML('afterend','&nbsp;'+remainingDaysFallback+' giorni al nuovo anno!')})()