document.addEventListener("DOMContentLoaded",function(){const bannerImg=document.getElementById("Banner");if(bannerImg){const banners=["https://gitechnolo.github.io/biotechproject/Biotech-file/images/Biotech-menu/banner1.avif","https://gitechnolo.github.io/biotechproject/Biotech-file/images/Biotech-menu/banner2.avif","https://gitechnolo.github.io/biotechproject/Biotech-file/images/Biotech-menu/banner3.avif","https://gitechnolo.github.io/biotechproject/Biotech-file/images/Biotech-menu/banner4.avif"];const preloadedImages=banners.map(src=>{const img=new Image();img.src=src;return img});let bnrCntr=0;function cycleBanner(){bannerImg.classList.add("banner-fade-out");setTimeout(()=>{bnrCntr=(bnrCntr+1)%banners.length;bannerImg.src=banners[bnrCntr];bannerImg.classList.remove("banner-fade-out")},500)}
setInterval(cycleBanner,3500)}
function getNextHoliday(){const now=new Date();const year=now.getFullYear();const holidays=[{name:"St. Patrick's Day",date:new Date(`${year}-03-17`),style:"st-patrick"},{name:"4th of July",date:new Date(`${year}-07-04`),style:"july4"},{name:"Halloween",date:new Date(`${year}-10-31`),style:"halloween"},{name:"Christmas",date:new Date(`${year}-12-25`),style:"natale"}];if(now>holidays[3].date){holidays[0].date=new Date(`${year + 1}-03-17`)}
const nextHoliday=holidays.find(h=>h.date>now)||holidays[0];const daysLeft=Math.ceil((nextHoliday.date-now)/(1000*60*60*24));return{msg:`Only ${daysLeft} days until <span class="holiday-name ${nextHoliday.style}">${nextHoliday.name}</span>!`,style:nextHoliday.style}}
const msgContainer=document.getElementById("holidayMsg");if(msgContainer){const holiday=getNextHoliday();msgContainer.innerHTML=`<p class="holiday-countdown ${holiday.style}">${holiday.msg}</p>`}});function turnOnLight(){const img=document.getElementById('myImage');if(img){img.src='https://gitechnolo.github.io/biotechproject/Biotech-file/images/pic_bulbon.avif';img.classList.add('bulb-glow')}}
function turnOffLight(){const img=document.getElementById('myImage');if(img){img.src='https://gitechnolo.github.io/biotechproject/Biotech-file/images/pic_bulboff.avif';img.classList.remove('bulb-glow')}}(function(){const initFadeInSequence=()=>{const lines=document.querySelectorAll(".type-line");let currentLineIndex=0;const showNextLine=()=>{if(currentLineIndex>=lines.length)return;const line=lines[currentLineIndex];const text=line.textContent.trim();if(text.length===0){setTimeout(showNextLine,50);return}
line.classList.add("is-visible");currentLineIndex++;setTimeout(showNextLine,500)};const observer=new IntersectionObserver((entries)=>{if(entries[0].isIntersecting){showNextLine();observer.disconnect()}},{threshold:0.2});const terminalWindow=document.querySelector(".terminal-window");if(terminalWindow)observer.observe(terminalWindow);};window.addEventListener("load",initFadeInSequence)})()